SELECT CNTRYCODE, COUNT(*) AS NUMCUST, SUM(C_ACCTBAL) AS TOTACCTBAL FROM %PARALLEL ( SELECT SUBSTRING(C_PHONE,1,2) AS CNTRYCODE, C_ACCTBAL FROM %PARALLEL CUSTOMER WHERE SUBSTRING(C_PHONE,1,2) IN ('18', '32', '21', '27', '13', '29', '22') AND C_ACCTBAL > ( SELECT AVG(C_ACCTBAL) FROM %PARALLEL CUSTOMER WHERE C_ACCTBAL > 0.00 AND SUBSTRING(C_PHONE,1,2) IN ('18', '32', '21', '27', '13', '29', '22')) AND NOT EXISTS ( SELECT * FROM %PARALLEL ORDERS WHERE O_CUSTKEY = C_CUSTKEY)) AS CUSTSALE GROUP BY CNTRYCODE ORDER BY CNTRYCODE
;
